<template>
  <div
    class="search-container"
    style="min-width: 1200px"
    v-loading="filterLoading"
  >
    <div class="search">
      <div class="search-detail">
        <div style="display: flex; align-items: center">
          <div class="search-logo">
            <img src="../../assets/images/search-logo.png" alt="" />
          </div>
          <div class="search-input" style="margin-left: 10px">
            <el-input
              v-model="queryAll.keyword"
              size="large"
              class="input"
              clearable
              placeholder="搜索关键词、行业、公司等…（多个关键词可用空格分开）"
            >
              <template #append>
                <el-button
                  @click="inputSearch('input')"
                  :icon="Search"
                ></el-button>
              </template>
            </el-input>
          </div>
        </div>
        <div class="search-hot">
          <span class="tip">热门搜索：</span>
          <span
            class="hover"
            @click="inputSearch('hot', item.hotWord)"
            v-for="item in hotWordsList"
            :key="item.hotWord"
            >{{ item.hotWord }}</span
          >
        </div>
      </div>
    </div>
    <div class="filter-detail" v-if="articleClassList.length > 0">
      <div class="filter-content">
        <div
          class="filter-item"
          v-for="item in articleClassList"
          :key="item.firstClassId"
        >
          <div class="filter-label">
            <span>
              <img :src="item.firstClassIconUrl" alt="" />
              {{ item.firstClassName }}
            </span>
          </div>
          <div class="filter-options">
            <!-- 单选 -->
            <template v-if="item.radioOrMore === '0'">
              <el-radio-group
                @change="checkboxChange"
                v-model="bindFilterKeys[item.key]"
              >
                <el-radio
                  v-for="citem in item.secondClassList"
                  :key="citem.secondClassId"
                  :label="citem.secondClassId"
                  >{{ citem.secondClassName }}</el-radio
                >
              </el-radio-group>
            </template>
            <!-- 多选 -->
            <template v-if="item.radioOrMore === '1'">
              <el-checkbox-group
                @change="checkboxChange"
                v-model="bindFilterKeys[item.key]"
              >
                <el-checkbox
                  v-for="citem in item.secondClassList"
                  :key="citem.secondClassId"
                  :label="citem.secondClassId"
                  >{{ citem.secondClassName }}</el-checkbox
                >
              </el-checkbox-group>
            </template>
          </div>
        </div>
      </div>
    </div>
    <div
      class="content-list"
      v-loading="listLoading"
      v-show="articleList.length > 0"
    >
      <div class="content">
        <div class="report-brief flex">
          <div>
            <span
              @click="sortClick(0)"
              :class="{ active: queryAll.sort === 0 }"
              class="sort-total pointer"
              >综合排序</span
            >
            <span
              @click="sortClick(1)"
              :class="{ active: queryAll.sort !== 0 }"
              class="sort-time pointer"
              >时间排序
              <span class="icon down">
                <img
                  v-if="queryAll.sort === 2 || queryAll.sort === 0"
                  :src="SJX_ICON"
                  alt="下三角"
                  class=""
                />
                <img
                  v-if="queryAll.sort === 1"
                  class="up"
                  :src="SJX_ICON_SH"
                  alt="上三角"
                />
              </span>
            </span>
          </div>
          <span class="total"
            >共找到<span style="margin: 0 4px">{{ countTotal || 0 }}</span
            >条结果</span
          >
        </div>
        <div class="report-list items">
          <div class="item" v-for="item in articleList" :key="item.articleId">
            <div class="flexrbc">
              <div class="item-content" @click="handelClick(item.articleId)">
                <div class="item-title">
                  <span class="hover">
                    <span>【{{ item.belong }}】</span>
                    <span>{{ item.title || '' }}</span>
                  </span>
                  <div class="labels" v-if="item.isDeep === '1'">
                    <img :src="SD_ICON" alt="深度" />
                  </div>
                  <div class="labels" v-if="item.isHot === '1'">
                    <img :src="HOT_ICON" alt="热门" />
                  </div>
                  <div class="labels" v-if="item.isNew === '1'">
                    <img :src="NEW_ICON" alt="新" />
                  </div>
                </div>
                <div class="item-badge flexr0c">
                  <template v-if="item.industry">
                    <span class="state state1">{{ item.industry }}</span>
                  </template>
                  <span class="state state1"
                    >{{ item.pages }}页*{{ item.size || 0 }}M</span
                  >
                  <div class="time">
                    <i
                      class="el-tooltip overflowtxt"
                      aria-describedby="el-tooltip-5823"
                      tabindex="0"
                      >{{ item.time || '' }}</i
                    >
                  </div>
                  <div class="operation flexr0c">
                    <i
                      class="el-tooltip overflowtxt"
                      aria-describedby="el-tooltip-8040"
                      tabindex="0"
                      >{{ item.belong || '' }}</i
                    >
                    <i
                      class="el-tooltip overflowtxt"
                      aria-describedby="el-tooltip-2503"
                      tabindex="0"
                      >{{ item.author || '' }}</i
                    >
                  </div>
                </div>
              </div>
              <div
                @click="downBtn(item)"
                class="item-download flexrcc"
                v-if="item.word || item.ppt || item.pdf"
              >
                <span style="margin-right: 5px">下载</span>
                <div class="flexr0c">
                  <img
                    v-if="item.word"
                    :src="WORD"
                    alt="WORD"
                    class="icon"
                    style="margin-right: 5px"
                  />
                  <img
                    v-if="item.pdf"
                    :src="PDF"
                    alt="PDF"
                    class="icon"
                    style="margin-right: 5px"
                  />
                  <img v-if="item.ppt" :src="PPT" alt="PPT" class="icon" />
